#!/usr/bin/env python3
import json, hashlib
from pathlib import Path

def sha3_hex(b: bytes) -> str:
    return hashlib.sha3_256(b).hexdigest()

def main():
    import argparse
    ap = argparse.ArgumentParser()
    ap.add_argument('--artifact', required=True)
    ap.add_argument('--outdir', required=True)
    args = ap.parse_args()

    art_bytes = Path(args.artifact).read_bytes()
    a = json.loads(art_bytes.decode('utf-8'))

    expected = a.get('self_hash', {}).get('sha3_256')
    if expected:
        actual = sha3_hex(art_bytes)
        if actual != expected:
            raise SystemExit(f"Artifact sha3 mismatch: {actual} != {expected}")

    outdir = Path(args.outdir)
    for f in a['bundle_files']:
        p = outdir / f['path']
        if not p.exists():
            raise SystemExit(f"Missing file: {f['path']}")
        data = p.read_bytes()
        h = sha3_hex(data)
        if h != f['sha3_256']:
            raise SystemExit(f"File sha3 mismatch: {f['path']} {h} != {f['sha3_256']}")

    print('VERIFY_OK')

if __name__ == '__main__':
    main()
